import  {useAuth} from "../Context/LoginContext";
import {useNavigate,useLocation  } from "react-router-dom";
import React, { useState } from "react";
import {Form ,Field,appStyle,formStyle,inputStyle,labelStyle,submitStyle} from '../Login_Form/logindesign'



export default function Login(){
  
    const {login,LoginWithCredential,setlogin} = useAuth();
    let user = "";
    let pass = "";
    const { state } = useLocation();
    const navigate =  useNavigate();
    // const user = localStorage.getItem('isuserlogin'); 

    function ClickHandler(event){
        event.preventDefault();
          LoginWithCredential(user,pass);
          navigate(state?.from? state.from:'/')


    // setlogin((login)=>!login);
    
    
 }

    return (
        <div style={appStyle}> 
<form style={formStyle} >
        <input  onChange={(text)=>{
                   user = text.target.value;
                }} label="Username:" type="text" />
        <input  onChange={(text)=>{
                    pass = text.target.value;
                }} label="Password:" type="password" />
        <div>
          <button onClick={ClickHandler} style={submitStyle} type="submit">{login?"Logout":"Login"}</button>
        </div>
      </form>
</div>
       
       
        );
}
