import ReactPlayer from 'react-player';
import { useParams ,useQuery,useLocation} from 'react-router-dom';
export default function WatchVideo(){
    let { videoId } = useParams();
    let query = URLSearchParams(useLocation().search);;
    return(
            <div className="WatchVideoPage">

            <ReactPlayer playing={true} url={`https://youtu.be/${videoId}`} controls={true} height="413px" width="729px"/>
              
            <h1> name={query.get("title")} </h1>
            </div>
        
    
        

    );
}
