import React, { useState } from "react";
import  {useAuth} from "../";
export const appStyle = {
	height: '250px',
  	display: 'flex',
      marginTop: "5rem"
};


export const formStyle = {
    margin: 'auto',
    padding: '10px',
    border: '1px solid #c9c9c9',
    borderRadius: '5px',
    background: '#f5f5f5',
    width: '220px',
  	display: 'block'
};

export const labelStyle = {
    margin: '10px 0 5px 0',
    fontFamily: 'Arial, Helvetica, sans-serif',
    fontSize: '15px',
};

export const inputStyle = {
    margin: '5px 0 10px 0',
    padding: '5px', 
    border: '1px solid #bfbfbf',
    borderRadius: '3px',
    boxSizing: 'border-box',
    width: '100%'
};

export const submitStyle = {
    margin: '10px 0 0 0',
    padding: '7px 10px',
    border: '1px solid #efffff',
    borderRadius: '3px',
    background: '#3085d6',
    width: '100%', 
    fontSize: '15px',
    color: 'white',
    display: 'block',
    cursor:'pointer'
};


export default function Form(){

    const {login,LoginWithCredential,setlogin,check} = useAuth();
    let user = "";
    let pass = "";
    const userId = localStorage.getItem('userId');


 
    function ClickHandler(event){
        event.preventDefault();
         LoginWithCredential(user,pass);
        
        // navigate(state?.from? state.from:'/login')
 }

 






    return (
        <form style={formStyle} >
            
        <div style={alignment}><small ><b>Username:</b></small></div>
         <input  onChange={(text)=>{
                    user = text.target.value;
                 }} label="Username:" type="text" />
                 <div style={alignment}><small ><b>Password:</b></small></div>
         <input  onChange={(text)=>{
                     pass = text.target.value;
                 }} label="Password:" type="password" />
         <div>
           <button onClick={ClickHandler} style={submitStyle} type="submit">Login</button>
         </div>

         <div>
           <Link to="/signup"><button style={submitStyle}>Register Yourself</button></Link>
         </div>

       </form>);
}
