import  {useAuth} from "../Context/LoginContext";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {useNavigate,useLocation, Link  } from "react-router-dom";
import React, { useEffect } from "react";
import {Toast} from "../Utils/Toast"
import {appStyle,formStyle,submitStyle} from '../Utils/logindesign'
import Form from '../Utils/logindesign'




export default function Login(){
     const {login,LoginWithCredential,setlogin,check} = useAuth();
    // const { state } = useLocation();
    // const navigate =  useNavigate();

    useEffect(()=>{
      check();
    },[]);
 

 
     
 
    function Logouthandler(){
      setlogin(false);
      localStorage.clear();
      toast.success("Successfull Logout!")
   }
  
  




    return (
    <div className="LoginFrame">
    {
        login
        ? <button  onClick={Logouthandler}style={submitStyle}>Logout</button>
        :    <Form/>
        
    }
    <div>
    
    {Toast()}
    </div>
    </div>
       
        );
}
